let slideItem=document.querySelectorAll(".slider-item"),totalSlides=slideItem.length,sliderHeight=document.querySelector(".slider").clientHeight,slideWrap=document.querySelector(".slider-wrap"),currentSlide=0;function goPrev(){currentSlide--,currentSlide<0&&(currentSlide=totalSlides-1),updateSlide()}function goNext(){currentSlide++,currentSlide>totalSlides-1&&(currentSlide=0),updateSlide()}function updateSlide(){let newMargin=currentSlide*slideItem[0].clientWidth;slideWrap.style.marginLeft=`-${newMargin}px`}slideWrap.style.width=`calc(100vw * ${totalSlides})`,document.querySelector(".slider-controls").style.height=`${sliderHeight}px`,setInterval(goNext,5e3),document.querySelector(".slider-control-prev").addEventListener("click",goPrev),document.querySelector(".slider-control-next").addEventListener("click",goNext);